    static HashSet<String> data;
    
    protected DocReader(File[] file) 
    {
        //Initialize the file array with all the files selected by the user
        this.file = file;
        
        //To store all the email address'
        data = new HashSet<String>();
    }
    
    protected static void readFile()
    {
        //Iterate over all files
        for(int i=0;i<file.length;i++)
        {
            //Send to appropriate method for reading
            
            if(file[i].getName().toLowerCase().endsWith(".doc"))
            {
                readDocFile(file[i]);
            }
            else if(file[i].getName().toLowerCase().endsWith(".docx"))
            {
                readDocxFile(file[i]);
            }
        }
        
        //Show all the email in the TextArea        
        CV_Extractor.showInTetxArea(data);
    }
    
    protected static void readDocFile(File localFile) 
    {
        try
            {
                //Create a input stream to read file
                FileInputStream fis = new FileInputStream(localFile.getAbsolutePath());

                //For reading docx files
                HWPFDocument doc = new HWPFDocument(fis);

                WordExtractor we = new WordExtractor(doc);

                String[] paragraphs = we.getParagraphText();
		
                System.out.println("Total no of paragraph "+paragraphs.length);
		 
                for (String para : paragraphs)
                {
                    //Compile the regex defined above
                    Pattern r = Pattern.compile(pattern);
                    
                    //Check if any string matches the compiled pattern
                    Matcher m = r.matcher(para);
                    
                    if(m.find())
                    {
                        //m.group() Returns the input subsequence matched by the previous match
                        data.add(m.group());
                    }
                }
                
                fis.close();
	    }
            
            catch (Exception e) 
            {
                e.printStackTrace();
            }
    }
    
    protected static void readDocxFile(File localFile) 
    {
        try 
            {
                //Create a input stream to read file
		FileInputStream fis = new FileInputStream(localFile.getAbsolutePath());

                //For reading docx files
                XWPFDocument document = new XWPFDocument(fis);

                List<XWPFParagraph> paragraphs = document.getParagraphs();
		
                System.out.println("Total no of paragraph "+paragraphs.size());
                                              
                for (XWPFParagraph para : paragraphs) 
                {
                    //Compile the regex defined above
                    Pattern r = Pattern.compile(pattern);
                    
                    //Check if any string matches the compiled pattern
                    Matcher m = r.matcher(para.getText());
                    
                    if(m.find())
                    {
                        //m.group() Returns the input subsequence matched by the previous match
                        data.add(m.group());
                    }
                }
                
                fis.close();
               
            }
            catch (Exception e) 
            {
                e.printStackTrace();
            }
    }
    
    protected static void generateExcel(String directory, String fileName)
    {
        XSSFWorkbook workbook = new XSSFWorkbook();
         
        //Create a blank sheet
        XSSFSheet sheet = workbook.createSheet("Email Address");
        
        //Create CellStyle for header (First row)
        CellStyle cellStyle = sheet.getWorkbook().createCellStyle();
        Font font = sheet.getWorkbook().createFont();
        font.setBold(true);
        font.setFontHeightInPoints((short) 16);
        cellStyle.setFont(font);
 
        //Create first row (Header)
        Row header = sheet.createRow(0);
        
        //Creating a cell (Only one required, for single attribute E-mail)
        Cell cellTitle = header.createCell(1); 
        cellTitle.setCellStyle(cellStyle);
        cellTitle.setCellValue("E-Mail");
        
        //Counter to use while creating further rows, starting from row 1
        int rowNum = 1;
          
        //Iterate over data and write to sheet   
        Iterator <String> iterator = data.iterator();
        
        while (iterator.hasNext()) 
        {  
            //Create a row for each E-mail string
            Row row = sheet.createRow(rowNum++);
                       
            //Create a cell to put the E-mail string
            Cell cell = row.createCell(1);
            
            //Put value in the cell
            cell.setCellValue(iterator.next());
        }
        
        try
        {  
            //Write the workbook in file system
            
            if(fileName.equals(""))
            {
                fileName = "Email List";
            }
            
            FileOutputStream out = new FileOutputStream(new File(directory+"/"+fileName+".xlsx"));
            workbook.write(out);
            out.close();
            
            JOptionPane.showMessageDialog(null,"File generated !");
            
        }
        catch (Exception e)
        {
            e.printStackTrace();
        }
    }
